import React from 'react';
import ReactDOM from 'react-dom';

//class DeleteButton extends React.Component { }

class TodoListRow extends React.Component {
	render() {
		const row = this.props.tasks.map((item, index) => {
			return (
				<tr>
					<td key={index}>{item}</td>
				</tr>
			);
		});
		return <tbody>{row}</tbody>;
	}
}
class TodoList extends React.Component {
	render() {
		return (
			<table>
				<TodoListRow tasks={this.props.tasks} />
			</table>
		);
	}
}
class TodoInput extends React.Component {
	add = () => {
		let inp = document.getElementById("inp");
		this.props.onAdd(inp.value);
		inp.value = "";
	};
	render() {
		return (
			<div className="Input">
				<input type="text" id="inp" placeholder="enter task todo" />
				<button onClick={this.add}>Add</button>
			</div>
		);
	}
}
class TodoApp extends React.Component {
	constructor(props) {
		super(props);
		this.state = { items: [] };
	}

	addTasks = tasks => {
		this.setState(this.state.items.concat(tasks));
	};

	render() {
		return (
			<div className="app">
				<h1>Todo App</h1>
				<TodoInput onAdd={this.addTasks} />
				<TodoList tasks={this.state.items} />
			</div>
		);
	}
}



ReactDOM.render(<TodoApp />, document.getElementById("root"));
